t.script:
(function () {
    var container = document.querySelector('.o_container[data-playback-url]');
    var playbackUrl = container ? container.dataset.playbackUrl : '';
    var video = document.getElementById('cctv-player');
    if (!playbackUrl) {
        return;
    }
    function safePlay() {
        var p = video.play();
        if (p && p.catch) {
            p.catch(function () {});
        }
    }
    if (video.canPlayType('application/vnd.apple.mpegurl')) {
        video.src = playbackUrl;
        safePlay();
    } else if (window.Hls && window.Hls.isSupported()) {
        var hls = new Hls();
        hls.loadSource(playbackUrl);
        hls.attachMedia(video);
        hls.on(Hls.Events.MEDIA_ATTACHED, function () {
            safePlay();
        });
    } else {
        video.outerHTML = '<div class="alert alert-danger mt-4">' +
            'Your browser cannot play HLS streams directly. ' +
            'Open the link above in VLC or a compatible player.' +
            '</div>';
    }
})();
